export async function createWorkingCopyConfig(
  files: FilesApi,
  gitDir: string,
  worktreeName?: string
): Promise<GitWorkingCopyConfig> {
  const configPath = worktreeName
    ? joinPath(gitDir, 'worktrees', worktreeName, 'config')
