import { GCController } from '@webrun-vcs/core';

// Get RawStoreWithDelta from repository (requires infrastructure task)
const deltaStore = repository.deltaStorage;

// Create GC controller
const gc = new GCController(deltaStore, {
  looseObjectThreshold: 50,
  chainDepthThreshold: 10,
  quickPackThreshold: 5,
});

// Run full GC with progress tracking
const result = await gc.runGC({
  maxChainDepth: 10,
  windowSize: 10,
  pruneLoose: false,
  progressCallback: (progress) => {
    console.log(`[${progress.phase}] ${progress.processedObjects}/${progress.totalObjects}`);
  },
});
