export class StatusCalculatorImpl implements StatusCalculator {
  async calculateStatus(
    workingCopy: WorkingCopy,
    options?: StatusOptions
  ): Promise<RepositoryStatus> {
    const { repository, worktree, staging } = workingCopy;

    const head = await workingCopy.getHead();
    const branch = await workingCopy.getCurrentBranch();

    // Compare:
    // 1. HEAD tree vs staging (staged changes)
    // 2. staging vs worktree (unstaged changes)
    // 3. Find untracked files in worktree

    return {
      branch,
      head,
      files: [...],
