describe("large objects", () => {
  it("handles large blobs (>16KB)", async () => {
    const pending = new PendingPack();

    // Create large blob (20KB - larger than typical 16KB buffer)
    const largeContent = new Uint8Array(20 * 1024);
    // Fill with pseudo-random data for realistic compression
    for (let i = 0; i < largeContent.length; i++) {
      largeContent[i] = (i * 31 + 17) % 256;
    }

    const objectId = await computeObjectId("blob", largeContent);
    pending.addObject(objectId, PackObjectType.BLOB, largeContent);

    const result = await pending.flush();

    // Verify pack was created
    expect(result.entries).toHaveLength(1);
    expect(result.entries[0].id).toBe(objectId);
    expect(result.packData.length).toBeGreaterThan(0);

    // Verify we can read it back
    const index = readPackIndex(result.indexData);
    const reader = new PackReader(files, packPath, index);
    await reader.open();

    const loaded = await reader.get(objectId);
    expect(loaded).toBeDefined();
    expect(loaded!.type).toBe(PackObjectType.BLOB);
    expect(loaded!.content).toEqual(largeContent);
    expect(loaded!.size).toBe(largeContent.length);

    await reader.close();
  });

  it("handles very large blobs (>1MB)", async () => {
    const pending = new PendingPack();

    // Create 1MB+ blob
    const veryLargeContent = new Uint8Array(1024 * 1024 + 100);
    for (let i = 0; i < veryLargeContent.length; i++) {
      veryLargeContent[i] = (i * 37 + 11) % 256;
    }

    const objectId = await computeObjectId("blob", veryLargeContent);
    pending.addObject(objectId, PackObjectType.BLOB, veryLargeContent);

    const result = await pending.flush();

    expect(result.entries).toHaveLength(1);

    // Verify roundtrip
    const index = readPackIndex(result.indexData);
    const reader = new PackReader(files, packPath, index);
    await reader.open();

    const loaded = await reader.get(objectId);
    expect(loaded!.content).toEqual(veryLargeContent);

    await reader.close();
  });

  it("handles large delta with large base", async () => {
    const pending = new PendingPack();

    // Large base object
    const baseContent = new Uint8Array(50 * 1024);
    for (let i = 0; i < baseContent.length; i++) {
      baseContent[i] = (i * 41) % 256;
    }
    const baseId = await computeObjectId("blob", baseContent);

    // Slightly modified target (should compress well as delta)
    const targetContent = new Uint8Array(baseContent);
    targetContent[0] = 0xff;
    targetContent[1000] = 0xee;
    const targetId = await computeObjectId("blob", targetContent);

    pending.addObject(baseId, PackObjectType.BLOB, baseContent);

    // Create delta
    const delta = createSimpleDelta(baseContent, targetContent);
    pending.addDelta(targetId, baseId, delta);

    const result = await pending.flush();
    expect(result.entries).toHaveLength(2);

    // Verify delta resolves correctly
    const index = readPackIndex(result.indexData);
    const reader = new PackReader(files, packPath, index);
    await reader.open();

    const loaded = await reader.get(targetId);
    expect(loaded!.content).toEqual(targetContent);

    await reader.close();
  });
});
