export function generateMarkdownReport(results: BenchmarkResult[]): string {
  let md = "# Delta Compression Benchmarks\n\n";
  md += "| Test | Source | Target | Delta | Ratio | Encode | Decode |\n";
  md += "|------|--------|--------|-------|-------|--------|--------|\n";

  for (const r of results) {
    md += `| ${r.name} | ${formatSize(r.sourceSize)} | ${formatSize(r.targetSize)} | `;
    md += `${formatSize(r.deltaSize)} | ${(r.compressionRatio * 100).toFixed(1)}% | `;
    md += `${r.encodeSpeedMBs.toFixed(1)} MB/s | ${r.decodeSpeedMBs.toFixed(1)} MB/s |\n`;
  }

  return md;
}
```

