```typescript
import type { ObjectId } from '../id/index.js';
import type { StashStore, StashEntry } from '../working-copy.js';

export class MemoryStashStore implements StashStore {
  private entries: StashEntry[] = [];

  async *list(): AsyncIterable<StashEntry> {
    for (const entry of this.entries) {
      yield entry;
    }
  }

  async push(message?: string): Promise<ObjectId> {
    const commitId = generateTestObjectId();
    const entry: StashEntry = {
      index: 0,
      commitId,
      message: message ?? 'WIP',
      timestamp: Date.now()
    };

    // Increment index of existing entries
    this.entries = this.entries.map(e => ({
      ...e,
      index: e.index + 1
    }));

    // Add new entry at front
    this.entries.unshift(entry);

    return commitId;
  }

  async pop(): Promise<void> {
    await this.apply(0);
    await this.drop(0);
  }

  async apply(index: number = 0): Promise<void> {
    if (index >= this.entries.length) {
      throw new Error(`No stash entry at index ${index}`);
    }
    // In real impl, would apply changes to working tree
  }

  async drop(index: number = 0): Promise<void> {
    if (index >= this.entries.length) {
      throw new Error(`No stash entry at index ${index}`);
    }
    this.entries.splice(index, 1);
    // Renumber remaining entries
    this.entries = this.entries.map((e, i) => ({
      ...e,
      index: i
    }));
  }

  async clear(): Promise<void> {
    this.entries = [];
  }
}
```

