## Key Implementation

### Document Decomposer (`src/document-decomposer.ts`)

```typescript
import JSZip from "jszip";

export interface DocumentComponents {
  files: Map<string, Uint8Array>;
  metadata: {
    type: "docx" | "odf";
    created?: Date;
    modified?: Date;
  };
}

export async function decomposeDocument(file: File): Promise<DocumentComponents> {
  const zip = await JSZip.loadAsync(file);
  const files = new Map<string, Uint8Array>();

  for (const [path, zipEntry] of Object.entries(zip.files)) {
    if (!zipEntry.dir) {
      const content = await zipEntry.async("uint8array");
      files.set(path, content);
    }
  }

  // Detect document type
  const type = files.has("[Content_Types].xml") ? "docx" : "odf";

  return { files, metadata: { type } };
}

export async function reconstructDocument(
  files: Map<string, Uint8Array>
): Promise<Blob> {
  const zip = new JSZip();

  for (const [path, content] of files) {
    zip.file(path, content);
  }

  return await zip.generateAsync({ type: "blob" });
}
```

### Version Tracker (`src/version-tracker.ts`)

```typescript
import { createGitRepository, FileMode, type GitRepository } from "@statewalker/vcs-core";
import type { FilesApi } from "@statewalker/webrun-files";

export class VersionTracker {
  constructor(private repo: GitRepository, private files: FilesApi) {}

  async saveVersion(
    components: Map<string, Uint8Array>,
    message: string
  ): Promise<string> {
    // Build tree entries from document components
    const entries = [];

    for (const [path, content] of components) {
      const blobId = await this.repo.blobs.store([content]);
      entries.push({
        mode: FileMode.REGULAR_FILE,
        name: path.replace(/\//g, "_"), // Flatten for simplicity
        id: blobId
      });
    }

    // Create tree
    const treeId = await this.repo.trees.storeTree(entries);

    // Get parent commit
    const head = await this.repo.getHead();
    const parents = head ? [head] : [];

    // Create commit
    const commitId = await this.repo.commits.storeCommit({
      tree: treeId,
      parents,
      author: {
        name: "Document User",
        email: "user@example.com",
        timestamp: Date.now() / 1000,
        tzOffset: "+0000"
      },
      committer: {
        name: "Document User",
        email: "user@example.com",
        timestamp: Date.now() / 1000,
        tzOffset: "+0000"
      },
      message
    });

    // Update ref
    await this.repo.refs.set("refs/heads/main", commitId);

    return commitId;
  }

  async getVersion(commitId: string): Promise<Map<string, Uint8Array>> {
    const commit = await this.repo.commits.loadCommit(commitId);
    const components = new Map<string, Uint8Array>();

    for await (const entry of this.repo.trees.loadTree(commit.tree)) {
      const chunks: Uint8Array[] = [];
      for await (const chunk of this.repo.blobs.load(entry.id)) {
        chunks.push(chunk);
      }
      components.set(entry.name.replace(/_/g, "/"), concat(chunks));
    }

    return components;
  }

  async *listVersions(): AsyncGenerator<{ id: string; message: string; date: Date }> {
    const head = await this.repo.getHead();
    if (!head) return;

    for await (const entry of this.repo.commits.walkAncestry(head)) {
      const commit = await this.repo.commits.loadCommit(entry.id);
      yield {
        id: entry.id,
        message: commit.message,
        date: new Date(commit.author.timestamp * 1000)
      };
    }
  }
}
```

