describe("delta chain resolution", () => {
  it("resolves 4-level REF_DELTA chain (Aâ†’Bâ†’Câ†’D)", async () => {
    // Create test data
    const baseContent = new Uint8Array(512).fill(0xf3);
    const baseId = await computeObjectId("blob", baseContent);

    // Create chain: base â†’ delta1 â†’ delta2 â†’ delta3
    const data1 = modifyFirst(0x01, baseContent);
    const delta1 = createDelta(baseContent, data1);
    const id1 = await computeObjectId("blob", data1);

    const data2 = modifyFirst(0x02, data1);
    const delta2 = createDelta(data1, data2);
    const id2 = await computeObjectId("blob", data2);

    const data3 = modifyFirst(0x03, data2);
    const delta3 = createDelta(data2, data3);
    const id3 = await computeObjectId("blob", data3);

    // Build pack with REF_DELTA chain
    const pending = new PendingPack();
    pending.addObject(baseId, PackObjectType.BLOB, baseContent);
    pending.addDelta(id1, baseId, delta1);
    pending.addDelta(id2, id1, delta2);
    pending.addDelta(id3, id2, delta3);

    const result = await pending.flush();

    // Create PackReader and verify chain resolution
    const index = readPackIndex(result.indexData);
    const reader = new PackReader(files, packPath, index);
    await reader.open();

    // Verify final object resolves correctly
    const loaded = await reader.get(id3);
    expect(loaded).toBeDefined();
    expect(loaded!.type).toBe(PackObjectType.BLOB);
    expect(loaded!.content).toEqual(data3);

    // Verify chain info
    const chainInfo = await reader.getDeltaChainInfo(id3);
    expect(chainInfo).toBeDefined();
    expect(chainInfo!.depth).toBe(3); // 3 deltas deep
    expect(chainInfo!.baseId).toBe(baseId);

    await reader.close();
  });

  it("resolves OFS_DELTA chain within same pack", async () => {
    // Similar test but with OFS_DELTA (base in same pack)
    // OFS_DELTA is more efficient when base is nearby
  });

  it("handles mixed OFS_DELTA and REF_DELTA chain", async () => {
    // Chain with both delta types
  });
});

// Helper functions
function modifyFirst(value: number, data: Uint8Array): Uint8Array {
  const result = new Uint8Array(data.length);
  result.set(data.subarray(1), 1);
  result[0] = value;
  return result;
}

function createDelta(base: Uint8Array, target: Uint8Array): Uint8Array {
  // Create binary delta: insert first byte, copy rest
  return new Uint8Array([
    ...encodeVarInt(base.length),    // base size
    ...encodeVarInt(target.length),  // result size
    0x01,                            // insert 1 byte
    target[0],                       // the new first byte
    0x80 | 0x01 | 0x10,             // copy command
    0x01,                            // offset = 1
    base.length - 1,                 // size = rest of data
  ]);
}
```

